
<!--begin::Form-->
<form class="m-form m-form--state m-form--fit m-form--label-align-right" id="config-medio-social" action="#" method="get">
    <div class="m-portlet__body">
        <div class="m-form__content">
            <div class="m-alert m-alert--icon alert alert-warning m--hide" role="alert" id="m_form_2_msg">
                <div class="m-alert__icon">
                    <i class="la la-warning"></i>
                </div>
                <div class="m-alert__text">
                    mensaje de error!
                </div>
                <div class="m-alert__close">
                    <button type="button" class="close" data-close="alert" aria-label="Close"></button>
                </div>
            </div>
        </div>

        <div class="form-group m-form__group row">
            <label for="facebook" class="col-form-label col-12 col-sm-3">
                Facebook
            </label>
            <div class="input-group col-12 col-sm-9 col-xl-4">
                <input id="facebook" type="text" class="form-control m-input" name="facebook" placeholder="https://www.facebook.com/" aria-describedby="basic-addon2">
                <span class="input-group-addon" id="basic-addon2">
                    <i class="la la-facebook"></i>
                </span>
            </div>
        </div>

        <div class="form-group m-form__group row">
            <label for="google" class="col-form-label col-12 col-sm-3">
                Google+
            </label>
            <div class="input-group col-12 col-sm-9 col-xl-4">
                <input id="google" type="text" class="form-control m-input" name="google" placeholder="https://www.google.com/" aria-describedby="basic-addon2">
                <span class="input-group-addon" id="basic-addon2">
                    <i class="la la-google-plus"></i>
                </span>
            </div>
        </div>

        <div class="form-group m-form__group row">
            <label for="twitter" class="col-form-label col-12 col-sm-3">
                Twitter
            </label>
            <div class="input-group col-12 col-sm-9 col-xl-4">
                <input id="twitter" type="text" class="form-control m-input" name="twitter" placeholder="https://www.twitter.com/" aria-describedby="basic-addon2">
                <span class="input-group-addon" id="basic-addon2">
                    <i class="la la-twitter"></i>
                </span>
            </div>
        </div>

        <div class="form-group m-form__group row">
            <label for="linkedin" class="col-form-label col-12 col-sm-3">
                LinkedIn
            </label>
            <div class="input-group col-12 col-sm-9 col-xl-4">
                <input id="linkedin" type="text" class="form-control m-input" name="linkedin" placeholder="https://www.linkedin.com/" aria-describedby="basic-addon2">
                <span class="input-group-addon" id="basic-addon2">
                    <i class="la la-linkedin"></i>
                </span>
            </div>
        </div>
    </div>
    <div class="m-portlet__foot m-portlet__foot--fit">
        <div class="m-form__actions m-form__actions">
            <div class="row">
                <div class="col-lg-9 ml-lg-auto">
                    <button type="submit" class="btn btn-success guardar">
                        Guardar
                    </button>
                    <button type="reset" class="btn btn-secondary">
                        Cancelar
                    </button>
                </div>
            </div>
        </div>
    </div>
</form>